create or replace VIEW BILLINGBUCKET_VW AS select lav.ACCOUNT_NO,lav.INSTITUTION_ID,bb."ACCOUNT_ID",bb."BILLING_BUCKET_NUMBER",bb."BILLING_BUCKET_STATUS",bb."BILL_CREATION_DATE",bb."PAYMENT_DUE_DATE",bb."PAST_DUE_DATE",bb."NO_OF_DAYS_BILL_PAST_DUE",bb."BILL_SATISFIED_DATE",bb."LAST_MAINTENANCE_DATE",bb."REPRINT_NOTICE_STATUS",bb."BILLED_PRINCIPAL",bb."BILLED_INTEREST",bb."BILLED_ESCROW",bb."BILLED_LATE_CHARGES",bb."BILLED_OTHER_FEES",bb."BILLED_SUBSIDY",bb."DELQ_NONESC",bb."BILLED_NONESCROW",bb."BILLED_CHARGE1",bb."BILLED_CHARGE2",bb."PAID_PRINCIPAL",bb."PAID_ESCROW",bb."PAID_INTEREST",bb."PAID_AH_LIFE",bb."PAID_CREDIT_LIFE",bb."PAID_LATE_FEE",bb."PAID_OTHER_FEE",bb."PAID_SUBSIDY",bb."BILLED_DEALER_INTEREST",bb."PAID_DEALER_INTEREST",bb."EXTENDED" from LOAN_ACCOUNT_VW lav,BILLING_BUCKET bb
where lav.ACCOUNT_ID=bb.ACCOUNT_ID(+);